  const [counter, setCounter] = useState([])

  const [visible, setVisible] = useState(false
    )

  return (
    <div>
      <div className="card-group">
        {visible && <div> <Card cardTitle="Trabzonspor" cardContent="karadeniz" />
        <Card cardTitle="Beşiktaş" cardContent="kartal" />
        <Card cardTitle="Galatasaray" cardContent="aslan" />
        <Button setCounter={setCounter} counter={counter}/></div>}
      </div>
    </div>
  )
}

export default App;
