import logo from './logo.svg';
import './App.css';
import Card from './components/Card';
import { useState, useEffect } from 'react'
import Button from './components/Button';

const App = () => {
  const [counter, setCounter] = useState([])

  const [visible, setVisible] = useState(true)


  return (
    <div>
      <div className="card-group">
        {visible && <div>
          <Card cardTitle="Trabzonspor" cardContent="karadeniz" />
          <Card cardTitle="Beşiktaş" cardContent="kartal" />
          <Card cardTitle="Galatasaray" cardContent="aslan" />
          <Button setCounter={setCounter} counter={counter} />
        </div>}
        <button onMouseOver={()=>setVisible(!visible)}>Visiblenin içindeki değiştirmek için kullanılan buton</button>
      </div>
    </div>
  )
}

export default App;
